portlet.script:
def detailButton(values) {
	def btn = new Button("Button $values.ID")
	btn.addListener({ showDetailsPopup(values) } as Button.ClickListener)
	return btn
}
def imageButton(values) {
	def btn = new Button()
	btn.addListener({ showDetailsPopup(values) } as Button.ClickListener)
	btn.styleName = Button.STYLE_LINK // wenn nur das Icon ohne Rahmen sichtbar sein soll
	// Theme Icon
	btn.icon = new com.vaadin.terminal.ThemeResource("../runo/icons/16/trash.png")
	// External Icon
	// btn.icon = new com.vaadin.terminal.ExternalResource('/html/themes/_unstyled/images/dock/add_content.png')
	return btn
}

def showDetailsPopup(values) {
	showPopup "Details", { 
		h2('Vaadin-Containerdaten') 
		table {
			values.each { key, value ->
				tr {
					td(key)
					td(value)
				}
			}
		}
	}
}
